const ShowData = [
  {
    showId: 12,
    showTitle: "제11회 정기공연",
    showTimes: "2025-09-25",
    showLocation: "서강대학교 메리홀 소극장",
    showPosterPicture:
      "data:image/png;base64,",
    isReservable: true,
  },
  {
    showId: 13,
    showTitle: "제12회 정기공연",
    showTimes: "2025-10-25",
    showLocation: "서강대학교 메리홀 대극장",
    showPosterPicture:
      "data:image/jpeg;base64,",
    isReservable: false,
  },
];

const UserData = [
  {
    showId: 12,
    showTitle: "제11회 정기공연",
    showTimes: "2025-09-25",
    showLocation: "서강대학교 메리홀 소극장",
    showPosterPicture:
      "data:image/png;base64,",

    isReservable: true,
  },
  {
    showId: 13,
    showTitle: "제12회 정기공연",
    showTimes: "2025-10-25",
    showLocation: "서강대학교 메리홀 대극장",
    showPosterPicture:
      "data:image/jpeg;base64,",

    isReservable: false,
  },
];

export default function HomeUser() {
  const [shows, setShows] = useState(ShowData);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userReservations, setUserReservations] = useState(UserData);
  const currentShow = useMemo(() => shows[currentIndex], [shows, currentIndex]);
  const navigate = useNavigate();

  function handleNext() {
    setCurrentIndex((prev) => Math.min(prev + 1, shows.length - 1));
  }
  function handlePrev() {
    setCurrentIndex((prev) => Math.max(prev - 1, 0));
  }

  const handleBuyTicket = () => {
    navigate("/viewshowdetail", {
      state: {
        managerId: currentShow.managerId,
        showId: currentShow.showId,
      },
    });
  };

  return (
    <PageWrapper>
      <HomeUserContainer>
        <NavbarUser />
        <ShowList>
          <button
            className="buttoncontainer"
            onClick={handlePrev}
            style={{ visibility: currentIndex === 0 ? "hidden" : "visible" }}
          >
            <RiArrowLeftWideFill size="40px" />
          </button>
          <ShowItemWrapper>
            <ShowItemSlider index={currentIndex}>
              {shows.map((show, idx) => {
                const isCurrentReservable =
                  idx === currentIndex && show.isReservable;
                return (
                  <ShowItem key={show.showId}>
                    <img
                      className="poster"
                      src={show.showPosterPicture}
                      alt={show.showTitle}
                      style={{
                        cursor: isCurrentReservable ? "pointer" : "",
                      }}
                      onClick={handleBuyTicket}
                    />
                    <div className="info">
                      <p>{show.showTimes}</p>
                      <h3>{show.showTitle}</h3>
                      <p>{show.showLocation}</p>
                    </div>
                  </ShowItem>
                );
              })}
            </ShowItemSlider>
            <Buyticketbtn
              reservable={currentShow.isReservable}
              onClick={handleBuyTicket}
            >
              {userReservations.includes(currentShow.showId)
                ? "예매 내역 확인하기"
                : "예매하기"}
            </Buyticketbtn>
          </ShowItemWrapper>

          <button
            className="buttoncontainer"
            onClick={handleNext}
            style={{
              visibility:
                currentIndex === shows.length - 1 ? "hidden" : "visible",
            }}
          >
            <RiArrowRightWideFill size="40px" />
          </button>
        </ShowList>
      </HomeUserContainer>
    </PageWrapper>
  );
}

